import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/Dashboard.css";

function Dashboard({ user, setUser }) {
  const navigate = useNavigate();
  const [projects, setProjects] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [stats, setStats] = useState({
    totalTasks: 0,
    overdueTasks: 0,
    todayTasks: 0,
    completedTasks: 0
  });
  const [showModal, setShowModal] = useState(false);
  const [showTaskModal, setShowTaskModal] = useState(false);
  const [selectedProject, setSelectedProject] = useState(null);
  const [newProject, setNewProject] = useState({
    title: "",
    description: "",
    teamMembers: []
  });
  const [newTask, setNewTask] = useState({
    title: "",
    description: "",
    project: "",
    dueDate: "",
    priority: "medium"
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [showNotifications, setShowNotifications] = useState(false);
  const [notifications, setNotifications] = useState([
    {
      id: 1,
      message: "New task assigned: Complete project documentation",
      time: "2 hours ago",
      photo: "https://ui-avatars.com/api/?name=John+Doe"
    },
    {
      id: 2,
      message: "Project deadline updated: Marketing Campaign",
      time: "5 hours ago",
      photo: "https://ui-avatars.com/api/?name=Jane+Smith"
    }
  ]);
  const [showProfile, setShowProfile] = useState(false);
  const [userPhoto, setUserPhoto] = useState(null);
  const [isDarkTheme, setIsDarkTheme] = useState(false);
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
  const [showSecurityModal, setShowSecurityModal] = useState(false);
  const [securitySettings, setSecuritySettings] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: '',
    twoFactorEnabled: false
  });
  const [recentActivity, setRecentActivity] = useState([
    {
      id: 1,
      type: 'login',
      description: 'Logged in from Chrome on Windows',
      timestamp: '2024-03-20T10:30:00',
      location: 'New York, USA',
      device: 'Chrome on Windows'
    },
    {
      id: 2,
      type: 'password_change',
      description: 'Password changed successfully',
      timestamp: '2024-03-19T15:45:00',
      location: 'New York, USA',
      device: 'Chrome on Windows'
    },
    {
      id: 3,
      type: 'login',
      description: 'Logged in from Safari on iPhone',
      timestamp: '2024-03-18T09:15:00',
      location: 'New York, USA',
      device: 'Safari on iPhone'
    }
  ]);
  const [loginStats, setLoginStats] = useState({
    totalLogins: 156,
    lastLogin: '2024-03-20T10:30:00',
    activeSessions: 2,
    loginMethods: {
      email: 120,
      google: 25,
      github: 11
    }
  });

  useEffect(() => {
    fetchProjects();
    fetchTasks();
    fetchStats();
    // Load theme preference from localStorage
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme) {
      setIsDarkTheme(savedTheme === 'dark');
      document.body.classList.toggle('dark-theme', savedTheme === 'dark');
    }

    // Fetch user profile photo
    const fetchUserPhoto = async () => {
      try {
        const response = await fetch('/api/users/profile', {
          credentials: 'include'
        });
        if (response.ok) {
          const data = await response.json();
          setUserPhoto(data.photo || null);
        }
      } catch (error) {
        console.error('Error fetching user photo:', error);
      }
    };

    fetchUserPhoto();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/projects", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setProjects(response.data);
      setError("");
    } catch (error) {
      console.error("Error fetching projects:", error);
      if (error.response?.status === 401) {
        setUser(null);
        navigate("/login", { replace: true });
      } else {
        setError("Failed to fetch projects. Please try again.");
      }
    }
  };

  const fetchTasks = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/tasks/my-tasks", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setTasks(response.data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/tasks/stats", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setStats(response.data);
    } catch (error) {
      console.error("Error fetching stats:", error);
    }
  };

  const handleLogout = async () => {
    setLoading(true);
    try {
      const response = await axios.get("http://localhost:5000/auth/logout", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });

      if (response.data.success) {
        setUser(null);
        navigate("/login", { replace: true });
      } else {
        console.error("Logout failed:", response.data.message);
      }
    } catch (error) {
      console.error("Logout error:", error.response?.data || error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateProject = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/projects", newProject, {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setShowModal(false);
      setNewProject({ title: "", description: "", teamMembers: [] });
      fetchProjects();
    } catch (error) {
      console.error("Error creating project:", error);
      if (error.response?.status === 401) {
        setUser(null);
        navigate("/login", { replace: true });
      } else {
        setError("Failed to create project. Please try again.");
      }
    }
  };

  const handleCreateTask = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/tasks", newTask, {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setShowTaskModal(false);
      setNewTask({
        title: "",
        description: "",
        project: "",
        dueDate: "",
        priority: "medium"
      });
      fetchTasks();
      fetchStats();
    } catch (error) {
      console.error("Error creating task:", error);
      setError("Failed to create task. Please try again.");
    }
  };

  const handleDeleteProject = async (projectId) => {
    if (!window.confirm("Are you sure you want to delete this project?")) {
      return;
    }

    try {
      await axios.delete(`http://localhost:5000/api/projects/${projectId}`, {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      fetchProjects();
    } catch (error) {
      console.error("Error deleting project:", error);
      setError("Failed to delete project. Please try again.");
    }
  };

  const handleTaskStatusChange = async (taskId, newStatus) => {
    try {
      await axios.patch(`http://localhost:5000/api/tasks/${taskId}/status`, 
        { status: newStatus },
        {
          withCredentials: true,
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
          }
        }
      );
      fetchTasks();
      fetchStats();
    } catch (error) {
      console.error("Error updating task status:", error);
      setError("Failed to update task status. Please try again.");
    }
  };

  const handleThemeToggle = () => {
    setIsDarkTheme(!isDarkTheme);
    document.body.classList.toggle('dark-theme');
  };

  const toggleSidebar = () => {
    setIsSidebarCollapsed(!isSidebarCollapsed);
  };

  const handleProfileClick = () => {
    setShowProfile(!showProfile);
    setShowNotifications(false);
  };

  const handleNotificationsClick = () => {
    setShowNotifications(!showNotifications);
    setShowProfile(false);
  };

  const handleSecurityUpdate = async (e) => {
    e.preventDefault();
    if (securitySettings.newPassword !== securitySettings.confirmPassword) {
      setError("New passwords don't match");
      return;
    }
    try {
      const response = await axios.post('/api/users/security/update', securitySettings, {
        withCredentials: true
      });
      setShowSecurityModal(false);
      setSecuritySettings({
        currentPassword: '',
        newPassword: '',
        confirmPassword: '',
        twoFactorEnabled: securitySettings.twoFactorEnabled
      });
      // Add new activity
      setRecentActivity(prev => [{
        id: Date.now(),
        type: 'password_change',
        description: 'Password changed successfully',
        timestamp: new Date().toISOString(),
        location: 'Current Location',
        device: navigator.userAgent
      }, ...prev]);
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to update security settings');
    }
  };

  const handleLogoutAllDevices = async () => {
    try {
      await axios.post('/api/auth/logout-all', {}, {
        withCredentials: true
      });
      setLoginStats(prev => ({
        ...prev,
        activeSessions: 1
      }));
      // Add new activity
      setRecentActivity(prev => [{
        id: Date.now(),
        type: 'logout_all',
        description: 'Logged out from all other devices',
        timestamp: new Date().toISOString(),
        location: 'Current Location',
        device: navigator.userAgent
      }, ...prev]);
    } catch (error) {
      setError('Failed to logout from all devices');
    }
  };

  return (
    <div className="dashboard">
      <div className="sidebar">
        <button className="sidebar-toggle" onClick={toggleSidebar}>
          <i className="fas fa-chevron-right"></i>
        </button>
        <div className="sidebar-header">
          <h2>CollabEase</h2>
        </div>
        <nav className="sidebar-nav">
          <button className="nav-item active" title="Dashboard">
            <i className="fas fa-home"></i>
            <span>Dashboard</span>
          </button>
          <button className="nav-item" title="Tasks">
            <i className="fas fa-tasks"></i>
            <span>Tasks</span>
          </button>
          <button className="nav-item" title="Projects">
            <i className="fas fa-project-diagram"></i>
            <span>Projects</span>
          </button>
          <button className="nav-item" title="Calendar">
            <i className="fas fa-calendar"></i>
            <span>Calendar</span>
          </button>
          <button className="nav-item" title="Team">
            <i className="fas fa-users"></i>
            <span>Team</span>
          </button>
          <button className="nav-item" title="Settings">
            <i className="fas fa-cog"></i>
            <span>Settings</span>
          </button>
        </nav>
      </div>
      <div className="main-content">
        <div className="dashboard-header">
          <h1>Dashboard</h1>
          <div className="header-actions">
            <button 
              className="icon-button theme-toggle" 
              onClick={handleThemeToggle}
              title={isDarkTheme ? "Switch to Light Mode" : "Switch to Dark Mode"}
            >
              <i className={`fas fa-${isDarkTheme ? 'sun' : 'moon'}`}></i>
            </button>

            <div className="notifications-container">
              <button 
                className="notification-btn" 
                onClick={() => setShowNotifications(!showNotifications)}
                title="Notifications"
              >
                <i className="fas fa-bell"></i>
                {notifications.length > 0 && (
                  <span className="notification-badge">{notifications.length}</span>
                )}
              </button>

              {showNotifications && (
                <div className="notifications-dropdown">
                  <div className="notifications-header">
                    <h3>Notifications</h3>
                    <button className="mark-all-read">Mark all as read</button>
                  </div>
                  <div className="notifications-list">
                    {notifications.length > 0 ? (
                      notifications.map((notification) => (
                        <div key={notification.id} className="notification-item">
                          <img 
                            src={notification.photo} 
                            alt="Notification" 
                            className="notification-photo"
                          />
                          <div className="notification-content">
                            <p>{notification.message}</p>
                            <span className="notification-time">{notification.time}</span>
                          </div>
                        </div>
                      ))
                    ) : (
                      <div className="no-notifications">No new notifications</div>
                    )}
                  </div>
                </div>
              )}
            </div>

            <div className="profile-container">
              <button 
                className="profile-btn" 
                onClick={() => setShowProfile(!showProfile)}
              >
                {userPhoto ? (
                  <img 
                    src={userPhoto} 
                    alt="Profile" 
                    className="profile-image"
                  />
                ) : (
                  <div className="profile-initial">
                    {user?.name?.charAt(0) || user?.email?.charAt(0)}
                  </div>
                )}
              </button>

              {showProfile && (
                <div className="profile-dropdown">
                  <div className="profile-info">
                    {userPhoto && (
                      <img 
                        src={userPhoto} 
                        alt="Profile" 
                        className="profile-image-large"
                      />
                    )}
                    <h3>{user?.name || 'User'}</h3>
                    <p>{user?.email}</p>
                  </div>
                  <div className="dropdown-divider"></div>
                  <button onClick={() => navigate('/profile')}>
                    <i className="fas fa-user"></i>
                    Profile
                  </button>
                  <button onClick={() => setShowSecurityModal(true)}>
                    <i className="fas fa-shield-alt"></i>
                    Security Settings
                  </button>
                  <button onClick={() => navigate('/settings')}>
                    <i className="fas fa-cog"></i>
                    Settings
                  </button>
                  <div className="dropdown-divider"></div>
                  <button className="logout-btn" onClick={handleLogout}>
                    <i className="fas fa-sign-out-alt"></i>
                    Logout
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>

        {error && <div className="error-message">{error}</div>}

        <div className="dashboard-stats">
          <div className="stat-card">
            <i className="fas fa-tasks"></i>
            <div className="stat-info">
              <h3>Total Tasks</h3>
              <p>{stats.totalTasks}</p>
            </div>
          </div>
          <div className="stat-card">
            <i className="fas fa-exclamation-circle"></i>
            <div className="stat-info">
              <h3>Overdue</h3>
              <p>{stats.overdueTasks}</p>
            </div>
          </div>
          <div className="stat-card">
            <i className="fas fa-calendar-day"></i>
            <div className="stat-info">
              <h3>Due Today</h3>
              <p>{stats.todayTasks}</p>
            </div>
          </div>
          <div className="stat-card">
            <i className="fas fa-check-circle"></i>
            <div className="stat-info">
              <h3>Completed</h3>
              <p>{stats.completedTasks}</p>
            </div>
          </div>
        </div>

        <div className="dashboard-content">
          <div className="section">
            <div className="section-header">
              <h2>My Projects</h2>
              <button className="add-btn" onClick={() => setShowTaskModal(true)}>
                <i className="fas fa-plus"></i> Add Task
              </button>
            </div>
            <div className="projects-grid">
              {projects.map((project) => (
                <div key={project._id} className="project-card">
                  <div className="project-header">
                    <h3>{project.title}</h3>
                    {project.owner._id === user?._id && (
                      <button 
                        className="delete-btn"
                        onClick={() => handleDeleteProject(project._id)}
                      >
                        <i className="fas fa-trash"></i>
                      </button>
                    )}
                  </div>
                  <p>{project.description}</p>
                  <div className="project-members">
                    <div className="member-list">
                      {project.teamMembers.map((member) => (
                        <div key={member._id} className="member-avatar" title={member.displayName}>
                          {member.image ? (
                            <img src={member.image} alt={member.displayName} />
                          ) : (
                            member.displayName[0]
                          )}
                        </div>
                      ))}
                    </div>
                    <span className="project-status">{project.status}</span>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="section">
            <h2>Recent Tasks</h2>
            <div className="tasks-list">
              {tasks.map((task) => (
                <div key={task._id} className="task-card">
                  <div className="task-header">
                    <h3>{task.title}</h3>
                    <select
                      value={task.status}
                      onChange={(e) => handleTaskStatusChange(task._id, e.target.value)}
                      className={`status-select ${task.status}`}
                    >
                      <option value="todo">To Do</option>
                      <option value="in-progress">In Progress</option>
                      <option value="completed">Completed</option>
                    </select>
                  </div>
                  <p>{task.description}</p>
                  <div className="task-footer">
                    <span className="task-project">{task.project.title}</span>
                    <span className={`task-priority ${task.priority}`}>
                      {task.priority}
                    </span>
                    {task.dueDate && (
                      <span className={`task-due-date ${new Date(task.dueDate) < new Date() ? 'overdue' : ''}`}>
                        Due: {new Date(task.dueDate).toLocaleDateString()}
                      </span>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="section">
            <div className="section-header">
              <h2>Recent Activity</h2>
            </div>
            <div className="activity-list">
              {recentActivity.map((activity) => (
                <div key={activity.id} className="activity-item">
                  <div className="activity-icon">
                    <i className={`fas fa-${activity.type === 'login' ? 'sign-in-alt' : 
                      activity.type === 'password_change' ? 'key' : 
                      activity.type === 'logout_all' ? 'sign-out-alt' : 'circle'}`}></i>
                  </div>
                  <div className="activity-content">
                    <p>{activity.description}</p>
                    <div className="activity-details">
                      <span><i className="fas fa-clock"></i> {new Date(activity.timestamp).toLocaleString()}</span>
                      <span><i className="fas fa-map-marker-alt"></i> {activity.location}</span>
                      <span><i className="fas fa-desktop"></i> {activity.device}</span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="section">
            <div className="section-header">
              <h2>Login Statistics</h2>
            </div>
            <div className="stats-grid">
              <div className="stat-card">
                <i className="fas fa-sign-in-alt"></i>
                <div className="stat-info">
                  <h3>Total Logins</h3>
                  <p>{loginStats.totalLogins}</p>
                </div>
              </div>
              <div className="stat-card">
                <i className="fas fa-clock"></i>
                <div className="stat-info">
                  <h3>Last Login</h3>
                  <p>{new Date(loginStats.lastLogin).toLocaleString()}</p>
                </div>
              </div>
              <div className="stat-card">
                <i className="fas fa-users"></i>
                <div className="stat-info">
                  <h3>Active Sessions</h3>
                  <p>{loginStats.activeSessions}</p>
                </div>
              </div>
            </div>
            <div className="login-methods">
              <h3>Login Methods</h3>
              <div className="method-bars">
                <div className="method-bar">
                  <span>Email</span>
                  <div className="bar-container">
                    <div className="bar" style={{ width: `${(loginStats.loginMethods.email / loginStats.totalLogins) * 100}%` }}></div>
                  </div>
                  <span>{loginStats.loginMethods.email}</span>
                </div>
                <div className="method-bar">
                  <span>Google</span>
                  <div className="bar-container">
                    <div className="bar" style={{ width: `${(loginStats.loginMethods.google / loginStats.totalLogins) * 100}%` }}></div>
                  </div>
                  <span>{loginStats.loginMethods.google}</span>
                </div>
                <div className="method-bar">
                  <span>GitHub</span>
                  <div className="bar-container">
                    <div className="bar" style={{ width: `${(loginStats.loginMethods.github / loginStats.totalLogins) * 100}%` }}></div>
                  </div>
                  <span>{loginStats.loginMethods.github}</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {showSecurityModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Security Settings</h2>
            <form onSubmit={handleSecurityUpdate}>
              <div className="security-section">
                <h3>Change Password</h3>
                <input
                  type="password"
                  placeholder="Current Password"
                  value={securitySettings.currentPassword}
                  onChange={(e) => setSecuritySettings({
                    ...securitySettings,
                    currentPassword: e.target.value
                  })}
                  required
                />
                <input
                  type="password"
                  placeholder="New Password"
                  value={securitySettings.newPassword}
                  onChange={(e) => setSecuritySettings({
                    ...securitySettings,
                    newPassword: e.target.value
                  })}
                  required
                />
                <input
                  type="password"
                  placeholder="Confirm New Password"
                  value={securitySettings.confirmPassword}
                  onChange={(e) => setSecuritySettings({
                    ...securitySettings,
                    confirmPassword: e.target.value
                  })}
                  required
                />
              </div>
              <div className="security-section">
                <h3>Two-Factor Authentication</h3>
                <label className="toggle-switch">
                  <input
                    type="checkbox"
                    checked={securitySettings.twoFactorEnabled}
                    onChange={(e) => setSecuritySettings({
                      ...securitySettings,
                      twoFactorEnabled: e.target.checked
                    })}
                  />
                  <span className="toggle-slider"></span>
                  Enable 2FA
                </label>
              </div>
              <div className="security-section">
                <h3>Active Sessions</h3>
                <p>Current active sessions: {loginStats.activeSessions}</p>
                <button type="button" className="danger-btn" onClick={handleLogoutAllDevices}>
                  Logout from all other devices
                </button>
              </div>
              <div className="modal-buttons">
                <button type="submit" className="submit-btn">
                  Save Changes
                </button>
                <button
                  type="button"
                  className="cancel-btn"
                  onClick={() => setShowSecurityModal(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
