import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { FaPlus, FaEllipsisH, FaCheck, FaClock, FaExclamationTriangle } from 'react-icons/fa';
import '../styles/KanbanBoard.css';

function KanbanBoard({ 
  tasks, 
  statuses, 
  onTaskMove, 
  onTaskCreate, 
  onTaskUpdate, 
  onTaskDelete,
  onStatusCreate,
  loading,
  error
}) {
  const [newTask, setNewTask] = useState({ title: '', description: '', status: '' });
  const [showNewTaskForm, setShowNewTaskForm] = useState(false);
  const [editingTask, setEditingTask] = useState(null);
  const [showNewStatusForm, setShowNewStatusForm] = useState(false);
  const [newStatus, setNewStatus] = useState('');

  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const { source, destination, draggableId } = result;
    if (source.droppableId === destination.droppableId) return;

    onTaskMove(draggableId, destination.droppableId);
  };

  const handleCreateTask = (status) => {
    setNewTask({ ...newTask, status });
    setShowNewTaskForm(true);
  };

  const handleSubmitTask = () => {
    if (!newTask.title) return;
    onTaskCreate(newTask);
    setNewTask({ title: '', description: '', status: '' });
    setShowNewTaskForm(false);
  };

  const handleCreateStatus = () => {
    if (!newStatus) return;
    onStatusCreate(newStatus);
    setNewStatus('');
    setShowNewStatusForm(false);
  };

  const getPriorityIcon = (priority) => {
    switch (priority?.toLowerCase()) {
      case 'high':
        return <FaExclamationTriangle className="priority-icon high" />;
      case 'medium':
        return <FaClock className="priority-icon medium" />;
      case 'low':
        return <FaCheck className="priority-icon low" />;
      default:
        return null;
    }
  };

  if (loading) {
    return <div className="kanban-loading">Loading board...</div>;
  }

  if (error) {
    return <div className="kanban-error">{error}</div>;
  }

  return (
    <DragDropContext onDragEnd={handleDragEnd}>
      <div className="kanban-board">
        {statuses.map((status) => (
          <div key={status} className="kanban-column">
            <div className="column-header">
              <h3>{status}</h3>
              <button 
                className="add-task-btn"
                onClick={() => handleCreateTask(status)}
              >
                <FaPlus />
              </button>
            </div>
            <Droppable droppableId={status}>
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  className="task-list"
                >
                  {tasks
                    .filter((task) => task.status === status)
                    .map((task, index) => (
                      <Draggable
                        key={task.id}
                        draggableId={task.id.toString()}
                        index={index}
                      >
                        {(provided) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                            className="task-card"
                          >
                            <div className="task-header">
                              <h4>{task.title}</h4>
                              <div className="task-actions">
                                {getPriorityIcon(task.priority)}
                                <button
                                  className="task-menu-btn"
                                  onClick={() => setEditingTask(task)}
                                >
                                  <FaEllipsisH />
                                </button>
                              </div>
                            </div>
                            <p className="task-description">{task.description}</p>
                            {task.tags && task.tags.length > 0 && (
                              <div className="task-tags">
                                {task.tags.map((tag) => (
                                  <span key={tag} className="task-tag">
                                    {tag}
                                  </span>
                                ))}
                              </div>
                            )}
                            {task.dueDate && (
                              <div className="task-due-date">
                                Due: {new Date(task.dueDate).toLocaleDateString()}
                              </div>
                            )}
                          </div>
                        )}
                      </Draggable>
                    ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </div>
        ))}
        <div className="kanban-column">
          <button
            className="add-column-btn"
            onClick={() => setShowNewStatusForm(true)}
          >
            <FaPlus /> Add Column
          </button>
        </div>
      </div>

      {/* New Task Form Modal */}
      {showNewTaskForm && (
        <div className="modal">
          <div className="modal-content">
            <h3>Create New Task</h3>
            <input
              type="text"
              placeholder="Task Title"
              value={newTask.title}
              onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
            />
            <textarea
              placeholder="Task Description"
              value={newTask.description}
              onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
            />
            <div className="modal-actions">
              <button onClick={handleSubmitTask}>Create</button>
              <button onClick={() => setShowNewTaskForm(false)}>Cancel</button>
            </div>
          </div>
        </div>
      )}

      {/* Edit Task Modal */}
      {editingTask && (
        <div className="modal">
          <div className="modal-content">
            <h3>Edit Task</h3>
            <input
              type="text"
              value={editingTask.title}
              onChange={(e) => setEditingTask({ ...editingTask, title: e.target.value })}
            />
            <textarea
              value={editingTask.description}
              onChange={(e) => setEditingTask({ ...editingTask, description: e.target.value })}
            />
            <div className="modal-actions">
              <button onClick={() => {
                onTaskUpdate(editingTask);
                setEditingTask(null);
              }}>Save</button>
              <button onClick={() => setEditingTask(null)}>Cancel</button>
              <button 
                className="delete-btn"
                onClick={() => {
                  onTaskDelete(editingTask.id);
                  setEditingTask(null);
                }}
              >
                Delete
              </button>
            </div>
          </div>
        </div>
      )}

      {/* New Status Form Modal */}
      {showNewStatusForm && (
        <div className="modal">
          <div className="modal-content">
            <h3>Add New Status</h3>
            <input
              type="text"
              placeholder="Status Name"
              value={newStatus}
              onChange={(e) => setNewStatus(e.target.value)}
            />
            <div className="modal-actions">
              <button onClick={handleCreateStatus}>Add</button>
              <button onClick={() => setShowNewStatusForm(false)}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </DragDropContext>
  );
}

export default KanbanBoard;
