import api from './api';

export const userService = {
  // Get all users
  getAllUsers: async () => {
    const response = await api.get('/users');
    return response.data;
  },

  // Get a single user
  getUser: async (id) => {
    const response = await api.get(`/users/${id}`);
    return response.data;
  },

  // Update user profile
  updateUser: async (id, userData) => {
    const response = await api.put(`/users/${id}`, userData);
    return response.data;
  },

  // Get user's projects
  getUserProjects: async (userId) => {
    const response = await api.get(`/users/${userId}/projects`);
    return response.data;
  },

  // Get user's tasks
  getUserTasks: async (userId) => {
    const response = await api.get(`/users/${userId}/tasks`);
    return response.data;
  },

  // Get user's notifications
  getUserNotifications: async (userId) => {
    const response = await api.get(`/users/${userId}/notifications`);
    return response.data;
  },

  // Mark notification as read
  markNotificationRead: async (userId, notificationId) => {
    const response = await api.put(`/users/${userId}/notifications/${notificationId}/read`);
    return response.data;
  },

  // Mark all notifications as read
  markAllNotificationsRead: async (userId) => {
    const response = await api.put(`/users/${userId}/notifications/read-all`);
    return response.data;
  }
};
