import { Routes, Route, Navigate } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

// Import pages
import Login from "./pages/Login";
import Register from "./pages/Register";

function App() {
	const [user, setUser] = useState(null);
	const [loading, setLoading] = useState(true);

	useEffect(() => {
		const getUser = async () => {
			try {
				const response = await axios.get("http://localhost:5000/auth/login/success", {
					withCredentials: true,
					headers: {
						'Content-Type': 'application/json',
						'Accept': 'application/json'
					}
				});

				console.log("Auth response:", response.data);

				if (response.data && response.data.user) {
					setUser(response.data.user);
				} else {
					console.log("No user data in response");
					setUser(null);
				}
			} catch (error) {
				console.log("Error fetching user:", error.response?.data || error.message);
				setUser(null);
			} finally {
				setLoading(false);
			}
		};
		getUser();
	}, []);

	if (loading) {
		return <div>Loading...</div>;
	}

	return (
		<div className="container">
			<Routes>
				<Route path="/login" element={user ? <Navigate to="/" /> : <Login />} />
				<Route path="/register" element={user ? <Navigate to="/" /> : <Register />} />
				<Route path="/" element={user ? <div>Welcome {user.username}</div> : <Navigate to="/login" />} />
			</Routes>
		</div>
	);
}

export default App;
