import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/Dashboard.css";

function Dashboard({ user, setUser }) {
  const navigate = useNavigate();
  const [projects, setProjects] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [newProject, setNewProject] = useState({
    title: "",
    description: "",
    teamMembers: []
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    // Fetch projects when component mounts
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/projects", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setProjects(response.data);
      setError("");
    } catch (error) {
      console.error("Error fetching projects:", error);
      if (error.response?.status === 401) {
        // If unauthorized, redirect to login
        setUser(null);
        navigate("/login", { replace: true });
      } else {
        setError("Failed to fetch projects. Please try again.");
      }
    }
  };

  const handleLogout = async () => {
    setLoading(true);
    try {
      const response = await axios.get("http://localhost:5000/auth/logout", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });

      if (response.data.success) {
        setUser(null); // Clear user state
        navigate("/login", { replace: true });
      } else {
        console.error("Logout failed:", response.data.message);
      }
    } catch (error) {
      console.error("Logout error:", error.response?.data || error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateProject = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/projects", newProject, {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setShowModal(false);
      setNewProject({ title: "", description: "", teamMembers: [] });
      fetchProjects();
    } catch (error) {
      console.error("Error creating project:", error);
      if (error.response?.status === 401) {
        setUser(null);
        navigate("/login", { replace: true });
      } else {
        setError("Failed to create project. Please try again.");
      }
    }
  };

  return (
    <div className="dashboard">
      <div className="sidebar">
        <div className="sidebar-header">
          <h2>CollabEase</h2>
        </div>
        <nav className="sidebar-nav">
          <button className="nav-item" onClick={() => navigate("/dashboard")}>
            <i className="fas fa-home"></i> Home
          </button>
          <button className="nav-item" onClick={() => navigate("/profile")}>
            <i className="fas fa-user"></i> Profile
          </button>
          <button 
            className="nav-item" 
            onClick={handleLogout}
            disabled={loading}
          >
            <i className="fas fa-sign-out-alt"></i> {loading ? "Logging out..." : "Logout"}
          </button>
        </nav>
      </div>

      <div className="main-content">
        <div className="dashboard-header">
          <h1>My Projects</h1>
          <button className="create-btn" onClick={() => setShowModal(true)}>
            Create Project
          </button>
        </div>

        {error && <div className="error-message">{error}</div>}

        <div className="projects-grid">
          {projects.map((project) => (
            <div key={project._id} className="project-card">
              <h3>{project.title}</h3>
              <p>{project.description}</p>
              <div className="project-members">
                {project.teamMembers.map((member) => (
                  <span key={member._id} className="member-avatar">
                    {member.username[0]}
                  </span>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Create New Project</h2>
            <form onSubmit={handleCreateProject}>
              <input
                type="text"
                placeholder="Project Title"
                value={newProject.title}
                onChange={(e) =>
                  setNewProject({ ...newProject, title: e.target.value })
                }
                required
              />
              <textarea
                placeholder="Project Description"
                value={newProject.description}
                onChange={(e) =>
                  setNewProject({ ...newProject, description: e.target.value })
                }
                required
              />
              <div className="modal-buttons">
                <button type="submit" className="submit-btn">
                  Create
                </button>
                <button
                  type="button"
                  className="cancel-btn"
                  onClick={() => setShowModal(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
