import { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import "../styles/Dashboard.css";
import { FaUserEdit, FaTrash } from 'react-icons/fa';

function Dashboard({ user, setUser }) {
  const navigate = useNavigate();
  const [projects, setProjects] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [stats, setStats] = useState({
    totalTasks: 0,
    overdueTasks: 0,
    todayTasks: 0,
    completedTasks: 0
  });
  const [showModal, setShowModal] = useState(false);
  const [showTaskModal, setShowTaskModal] = useState(false);
  const [selectedProject, setSelectedProject] = useState(null);
  const [newProject, setNewProject] = useState({
    title: "",
    description: "",
    teamMembers: []
  });
  const [newTask, setNewTask] = useState({
    title: "",
    description: "",
    project: "",
    dueDate: "",
    priority: "medium"
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [showNotifications, setShowNotifications] = useState(false);
  const [notifications, setNotifications] = useState([
    {
      id: 1,
      message: "New task assigned: Complete project documentation",
      time: "2 hours ago",
      photo: "https://ui-avatars.com/api/?name=John+Doe"
    },
    {
      id: 2,
      message: "Project deadline updated: Marketing Campaign",
      time: "5 hours ago",
      photo: "https://ui-avatars.com/api/?name=Jane+Smith"
    }
  ]);
  const [showProfile, setShowProfile] = useState(false);
  const [userPhoto, setUserPhoto] = useState(null);
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
  const [showSecurityModal, setShowSecurityModal] = useState(false);
  const [securitySettings, setSecuritySettings] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: '',
    twoFactorEnabled: false
  });
  const [recentActivity, setRecentActivity] = useState([
    {
      id: 1,
      type: 'login',
      description: 'Logged in from Chrome on Windows',
      timestamp: '2024-03-20T10:30:00',
      location: 'New York, USA',
      device: 'Chrome on Windows'
    },
    {
      id: 2,
      type: 'password_change',
      description: 'Password changed successfully',
      timestamp: '2024-03-19T15:45:00',
      location: 'New York, USA',
      device: 'Chrome on Windows'
    },
    {
      id: 3,
      type: 'login',
      description: 'Logged in from Safari on iPhone',
      timestamp: '2024-03-18T09:15:00',
      location: 'New York, USA',
      device: 'Safari on iPhone'
    }
  ]);
  const [loginStats, setLoginStats] = useState({
    totalLogins: 156,
    lastLogin: '2024-03-20T10:30:00',
    activeSessions: 2,
    loginMethods: {
      email: 120,
      google: 25,
      github: 11
    }
  });

  // Mock team members for demo
  const teamMembers = [
    {
      id: 1,
      name: 'Sanket Jadhav',
      email: 'sanket3280@gmail.com',
      status: 'Active',
      role: 'Member',
      avatar: '',
    },
    {
      id: 2,
      name: 'Sanket tanaji jadhav',
      email: 'sankettanaj.p24@medhaviskil...',
      status: 'Active',
      role: 'Member',
      avatar: '',
    },
    {
      id: 3,
      name: '25F1001327 SANKET JADHAV',
      email: '25f1001327@ds.study.iitm.ac...',
      status: 'Active',
      role: 'Member',
      avatar: '',
    },
    {
      id: 4,
      name: 'Sanket Jadhav',
      email: 'sj546400@gmail.com',
      status: 'Active',
      role: 'Member',
      avatar: 'https://ui-avatars.com/api/?name=Sanket+Jadhav&background=000000&color=fff',
    },
  ];
  const [showSuccess, setShowSuccess] = useState(true);

  useEffect(() => {
    fetchProjects();
    fetchTasks();
    fetchStats();
    // Load theme preference from localStorage
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme) {
      document.body.classList.toggle('dark-theme', savedTheme === 'dark');
    }

    // Fetch user profile photo
    const fetchUserPhoto = async () => {
      try {
        const response = await fetch('/api/users/profile', {
          credentials: 'include'
        });
        if (response.ok) {
          const data = await response.json();
          setUserPhoto(data.photo || null);
        }
      } catch (error) {
        console.error('Error fetching user photo:', error);
      }
    };

    fetchUserPhoto();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/projects", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setProjects(response.data);
      setError("");
    } catch (error) {
      console.error("Error fetching projects:", error);
      if (error.response?.status === 401) {
        setUser(null);
        navigate("/login", { replace: true });
      } else {
        setError("Failed to fetch projects. Please try again.");
      }
    }
  };

  const fetchTasks = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/tasks/my-tasks", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setTasks(response.data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/tasks/stats", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setStats(response.data);
    } catch (error) {
      console.error("Error fetching stats:", error);
    }
  };

  const handleLogout = async () => {
    setLoading(true);
    try {
      const response = await axios.get("http://localhost:5000/auth/logout", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });

      if (response.data.success) {
        setUser(null);
        navigate("/login", { replace: true });
      } else {
        console.error("Logout failed:", response.data.message);
      }
    } catch (error) {
      console.error("Logout error:", error.response?.data || error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateProject = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/projects", newProject, {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setShowModal(false);
      setNewProject({ title: "", description: "", teamMembers: [] });
      fetchProjects();
    } catch (error) {
      console.error("Error creating project:", error);
      if (error.response?.status === 401) {
        setUser(null);
        navigate("/login", { replace: true });
      } else {
        setError("Failed to create project. Please try again.");
      }
    }
  };

  const handleCreateTask = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/tasks", newTask, {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setShowTaskModal(false);
      setNewTask({
        title: "",
        description: "",
        project: "",
        dueDate: "",
        priority: "medium"
      });
      fetchTasks();
      fetchStats();
    } catch (error) {
      console.error("Error creating task:", error);
      setError("Failed to create task. Please try again.");
    }
  };

  const handleDeleteProject = async (projectId) => {
    if (!window.confirm("Are you sure you want to delete this project?")) {
      return;
    }

    try {
      await axios.delete(`http://localhost:5000/api/projects/${projectId}`, {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      fetchProjects();
    } catch (error) {
      console.error("Error deleting project:", error);
      setError("Failed to delete project. Please try again.");
    }
  };

  const handleTaskStatusChange = async (taskId, newStatus) => {
    try {
      await axios.patch(`http://localhost:5000/api/tasks/${taskId}/status`, 
        { status: newStatus },
        {
          withCredentials: true,
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
          }
        }
      );
      fetchTasks();
      fetchStats();
    } catch (error) {
      console.error("Error updating task status:", error);
      setError("Failed to update task status. Please try again.");
    }
  };

  const toggleSidebar = () => {
    setIsSidebarCollapsed(!isSidebarCollapsed);
  };

  const handleProfileClick = () => {
    setShowProfile(!showProfile);
    setShowNotifications(false);
  };

  const handleProfileNavigation = () => {
    setShowProfile(false);
    navigate('/profile');
  };

  const handleNotificationsClick = () => {
    setShowNotifications(!showNotifications);
    setShowProfile(false);
  };

  const handleSecurityUpdate = async (e) => {
    e.preventDefault();
    if (securitySettings.newPassword !== securitySettings.confirmPassword) {
      setError("New passwords don't match");
      return;
    }
    try {
      const response = await axios.post('/api/users/security/update', securitySettings, {
        withCredentials: true
      });
      setShowSecurityModal(false);
      setSecuritySettings({
        currentPassword: '',
        newPassword: '',
        confirmPassword: '',
        twoFactorEnabled: securitySettings.twoFactorEnabled
      });
      // Add new activity
      setRecentActivity(prev => [{
        id: Date.now(),
        type: 'password_change',
        description: 'Password changed successfully',
        timestamp: new Date().toISOString(),
        location: 'Current Location',
        device: navigator.userAgent
      }, ...prev]);
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to update security settings');
    }
  };

  const handleLogoutAllDevices = async () => {
    try {
      await axios.post('/api/auth/logout-all', {}, {
        withCredentials: true
      });
      setLoginStats(prev => ({
        ...prev,
        activeSessions: 1
      }));
      // Add new activity
      setRecentActivity(prev => [{
        id: Date.now(),
        type: 'logout_all',
        description: 'Logged out from all other devices',
        timestamp: new Date().toISOString(),
        location: 'Current Location',
        device: navigator.userAgent
      }, ...prev]);
    } catch (error) {
      setError('Failed to logout from all devices');
    }
  };

  const handleClickOutside = (event) => {
    if (showProfile && !event.target.closest('.profile-container')) {
      setShowProfile(false);
    }
    if (showNotifications && !event.target.closest('.notifications-container')) {
      setShowNotifications(false);
    }
  };

  useEffect(() => {
    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, [showProfile, showNotifications]);

  return (
    <div className="dashboard">
      {/* Top Navbar */}
      <nav className="main-navbar">
        <div className="navbar-left">
          <span className="navbar-logo">Taskboard-pro</span>
          <Link to="/calendar" className="navbar-link">Calendar</Link>
          <Link to="/tasks" className="navbar-link">My Tasks</Link>
          <Link to="/projects" className="navbar-link">Projects</Link>
          <Link to="/team" className="navbar-link">Team</Link>
          <Link to="/chat" className="navbar-link">Chat</Link>
        </div>
        <div className="navbar-right">
          <Link to="/profile" className="navbar-link">Profile</Link>
          <Link to="/settings" className="navbar-link">Settings</Link>
          <button className="icon-button logout-btn" onClick={handleLogout} title="Logout">
            <i className="fas fa-sign-out-alt"></i>
          </button>
        </div>
      </nav>
      <main className="main-content">
        {/* Top Bar */}
        <header className="topbar fixed-navbar">
          <div className="search-container">
            <input type="text" className="search-input" placeholder="Search projects, tasks, and team..." />
          </div>
          <div className="topbar-actions">
            <button className="icon-button" onClick={handleNotificationsClick} title="Notifications">
              <i className="fas fa-bell"></i>
              {notifications.length > 0 && <span className="notification-badge">{notifications.length}</span>}
            </button>
            <button className="icon-button profile-btn" onClick={handleProfileClick} title="Profile">
              {userPhoto ? (
                <img src={userPhoto} alt="Profile" className="profile-image" />
              ) : (
                <span className="profile-initial">{user?.name?.charAt(0) || user?.email?.charAt(0)}</span>
              )}
            </button>
            <button className="icon-button logout-btn" onClick={handleLogout} title="Logout">
              <i className="fas fa-sign-out-alt"></i>
            </button>
          </div>
        </header>

        {/* Welcome Message */}
        <div className="welcome-message">
          <h2>Welcome back, {user?.name}!</h2>
          <p>Here's what's happening in your projects today.</p>
        </div>

        {/* Stat Cards */}
        <section className="dashboard-stats-pro">
          <div className="stat-card-pro">
            <div className="stat-icon stat-projects"><i className="fas fa-folder"></i></div>
            <div className="stat-info">
              <div className="stat-title">Projects</div>
              <div className="stat-value">{projects.length}</div>
            </div>
          </div>
          <div className="stat-card-pro">
            <div className="stat-icon stat-tasks"><i className="fas fa-tasks"></i></div>
            <div className="stat-info">
              <div className="stat-title">Total Tasks</div>
              <div className="stat-value">{stats.totalTasks}</div>
            </div>
          </div>
          <div className="stat-card-pro">
            <div className="stat-icon stat-completed"><i className="fas fa-check-circle"></i></div>
            <div className="stat-info">
              <div className="stat-title">Completed</div>
              <div className="stat-value">{stats.completedTasks}</div>
            </div>
          </div>
          <div className="stat-card-pro">
            <div className="stat-icon stat-overdue"><i className="fas fa-exclamation-circle"></i></div>
            <div className="stat-info">
              <div className="stat-title">Overdue</div>
              <div className="stat-value">{stats.overdueTasks}</div>
            </div>
          </div>
        </section>

        {/* Main Dashboard Sections */}
        <div className="dashboard-sections-pro">
          <section className="my-tasks-section">
            <div className="section-header-pro">
              <h3>My Tasks</h3>
              <a href="#" className="view-all-link">View all</a>
            </div>
            <div className="my-tasks-list">
              {tasks.length === 0 ? <div className="empty-state">No tasks assigned to you</div> : null}
              {/* Map tasks here if needed */}
            </div>
          </section>
          <section className="recent-activity-section">
            <div className="section-header-pro">
              <h3>Recent Activity</h3>
              <a href="#" className="view-all-link">View all</a>
            </div>
            <div className="recent-activity-list">
              {recentActivity.length === 0 ? <div className="empty-state">No recent activity</div> : null}
              {/* Map recentActivity here if needed */}
            </div>
          </section>
        </div>
        <div className="dashboard-bottom-sections-pro">
          <section className="recent-projects-section">
            <div className="section-header-pro">
              <h3>Recent Projects</h3>
              <a href="#" className="view-all-link">View all</a>
            </div>
            <div className="recent-projects-list">
              {/* Map recent projects here if needed */}
            </div>
          </section>
          <section className="team-section">
            <div className="section-header-pro" style={{alignItems:'center'}}>
              <h3 style={{fontSize:'1.5rem'}}>Team Members</h3>
              <button className="add-team-btn">Add Team Member</button>
            </div>
            {showSuccess && (
              <div className="team-success-alert">User deleted successfully!</div>
            )}
            <div className="team-grid">
              {teamMembers.map(member => (
                <div className="team-card" key={member.id}>
                  <div className="team-card-avatar">
                    {member.avatar ? (
                      <img src={member.avatar} alt={member.name} />
                    ) : (
                      <span>{member.name[0]}</span>
                    )}
                  </div>
                  <div className="team-card-info">
                    <div className="team-card-name">{member.name}</div>
                    <div className="team-card-email">{member.email}</div>
                    <div className="team-card-badges">
                      <span className="badge badge-active">Active</span>
                      <span className="badge badge-role">Member</span>
                    </div>
                  </div>
                  <div className="team-card-actions">
                    <button className="team-action-btn"><FaUserEdit /></button>
                    <button className="team-action-btn" onClick={()=>setShowSuccess(true)}><FaTrash /></button>
                  </div>
                </div>
              ))}
            </div>
          </section>
        </div>
      </main>
    </div>
  );
}

export default Dashboard;
