import { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import "../styles/Dashboard.css";

function Dashboard({ user, setUser }) {
  const navigate = useNavigate();
  const [projects, setProjects] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [stats, setStats] = useState({
    totalTasks: 0,
    overdueTasks: 0,
    todayTasks: 0,
    completedTasks: 0
  });
  const [showModal, setShowModal] = useState(false);
  const [showTaskModal, setShowTaskModal] = useState(false);
  const [selectedProject, setSelectedProject] = useState(null);
  const [newProject, setNewProject] = useState({
    title: "",
    description: "",
    teamMembers: []
  });
  const [newTask, setNewTask] = useState({
    title: "",
    description: "",
    project: "",
    dueDate: "",
    priority: "medium"
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [showNotifications, setShowNotifications] = useState(false);
  const [notifications, setNotifications] = useState([
    {
      id: 1,
      message: "New task assigned: Complete project documentation",
      time: "2 hours ago",
      photo: "https://ui-avatars.com/api/?name=John+Doe"
    },
    {
      id: 2,
      message: "Project deadline updated: Marketing Campaign",
      time: "5 hours ago",
      photo: "https://ui-avatars.com/api/?name=Jane+Smith"
    }
  ]);
  const [showProfile, setShowProfile] = useState(false);
  const [userPhoto, setUserPhoto] = useState(null);
  const [isDarkTheme, setIsDarkTheme] = useState(false);
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
  const [showSecurityModal, setShowSecurityModal] = useState(false);
  const [securitySettings, setSecuritySettings] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: '',
    twoFactorEnabled: false
  });
  const [recentActivity, setRecentActivity] = useState([
    {
      id: 1,
      type: 'login',
      description: 'Logged in from Chrome on Windows',
      timestamp: '2024-03-20T10:30:00',
      location: 'New York, USA',
      device: 'Chrome on Windows'
    },
    {
      id: 2,
      type: 'password_change',
      description: 'Password changed successfully',
      timestamp: '2024-03-19T15:45:00',
      location: 'New York, USA',
      device: 'Chrome on Windows'
    },
    {
      id: 3,
      type: 'login',
      description: 'Logged in from Safari on iPhone',
      timestamp: '2024-03-18T09:15:00',
      location: 'New York, USA',
      device: 'Safari on iPhone'
    }
  ]);
  const [loginStats, setLoginStats] = useState({
    totalLogins: 156,
    lastLogin: '2024-03-20T10:30:00',
    activeSessions: 2,
    loginMethods: {
      email: 120,
      google: 25,
      github: 11
    }
  });

  useEffect(() => {
    fetchProjects();
    fetchTasks();
    fetchStats();
    // Load theme preference from localStorage
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme) {
      setIsDarkTheme(savedTheme === 'dark');
      document.body.classList.toggle('dark-theme', savedTheme === 'dark');
    }

    // Fetch user profile photo
    const fetchUserPhoto = async () => {
      try {
        const response = await fetch('/api/users/profile', {
          credentials: 'include'
        });
        if (response.ok) {
          const data = await response.json();
          setUserPhoto(data.photo || null);
        }
      } catch (error) {
        console.error('Error fetching user photo:', error);
      }
    };

    fetchUserPhoto();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/projects", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setProjects(response.data);
      setError("");
    } catch (error) {
      console.error("Error fetching projects:", error);
      if (error.response?.status === 401) {
        setUser(null);
        navigate("/login", { replace: true });
      } else {
        setError("Failed to fetch projects. Please try again.");
      }
    }
  };

  const fetchTasks = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/tasks/my-tasks", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setTasks(response.data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/tasks/stats", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setStats(response.data);
    } catch (error) {
      console.error("Error fetching stats:", error);
    }
  };

  const handleLogout = async () => {
    setLoading(true);
    try {
      const response = await axios.get("http://localhost:5000/auth/logout", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });

      if (response.data.success) {
        setUser(null);
        navigate("/login", { replace: true });
      } else {
        console.error("Logout failed:", response.data.message);
      }
    } catch (error) {
      console.error("Logout error:", error.response?.data || error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateProject = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/projects", newProject, {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setShowModal(false);
      setNewProject({ title: "", description: "", teamMembers: [] });
      fetchProjects();
    } catch (error) {
      console.error("Error creating project:", error);
      if (error.response?.status === 401) {
        setUser(null);
        navigate("/login", { replace: true });
      } else {
        setError("Failed to create project. Please try again.");
      }
    }
  };

  const handleCreateTask = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/tasks", newTask, {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setShowTaskModal(false);
      setNewTask({
        title: "",
        description: "",
        project: "",
        dueDate: "",
        priority: "medium"
      });
      fetchTasks();
      fetchStats();
    } catch (error) {
      console.error("Error creating task:", error);
      setError("Failed to create task. Please try again.");
    }
  };

  const handleDeleteProject = async (projectId) => {
    if (!window.confirm("Are you sure you want to delete this project?")) {
      return;
    }

    try {
      await axios.delete(`http://localhost:5000/api/projects/${projectId}`, {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      fetchProjects();
    } catch (error) {
      console.error("Error deleting project:", error);
      setError("Failed to delete project. Please try again.");
    }
  };

  const handleTaskStatusChange = async (taskId, newStatus) => {
    try {
      await axios.patch(`http://localhost:5000/api/tasks/${taskId}/status`, 
        { status: newStatus },
        {
          withCredentials: true,
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
          }
        }
      );
      fetchTasks();
      fetchStats();
    } catch (error) {
      console.error("Error updating task status:", error);
      setError("Failed to update task status. Please try again.");
    }
  };

  const handleThemeToggle = () => {
    setIsDarkTheme(!isDarkTheme);
    document.body.classList.toggle('dark-theme');
  };

  const toggleSidebar = () => {
    setIsSidebarCollapsed(!isSidebarCollapsed);
  };

  const handleProfileClick = () => {
    setShowProfile(!showProfile);
    setShowNotifications(false);
  };

  const handleProfileNavigation = () => {
    setShowProfile(false);
    navigate('/profile');
  };

  const handleNotificationsClick = () => {
    setShowNotifications(!showNotifications);
    setShowProfile(false);
  };

  const handleSecurityUpdate = async (e) => {
    e.preventDefault();
    if (securitySettings.newPassword !== securitySettings.confirmPassword) {
      setError("New passwords don't match");
      return;
    }
    try {
      const response = await axios.post('/api/users/security/update', securitySettings, {
        withCredentials: true
      });
      setShowSecurityModal(false);
      setSecuritySettings({
        currentPassword: '',
        newPassword: '',
        confirmPassword: '',
        twoFactorEnabled: securitySettings.twoFactorEnabled
      });
      // Add new activity
      setRecentActivity(prev => [{
        id: Date.now(),
        type: 'password_change',
        description: 'Password changed successfully',
        timestamp: new Date().toISOString(),
        location: 'Current Location',
        device: navigator.userAgent
      }, ...prev]);
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to update security settings');
    }
  };

  const handleLogoutAllDevices = async () => {
    try {
      await axios.post('/api/auth/logout-all', {}, {
        withCredentials: true
      });
      setLoginStats(prev => ({
        ...prev,
        activeSessions: 1
      }));
      // Add new activity
      setRecentActivity(prev => [{
        id: Date.now(),
        type: 'logout_all',
        description: 'Logged out from all other devices',
        timestamp: new Date().toISOString(),
        location: 'Current Location',
        device: navigator.userAgent
      }, ...prev]);
    } catch (error) {
      setError('Failed to logout from all devices');
    }
  };

  const handleClickOutside = (event) => {
    if (showProfile && !event.target.closest('.profile-container')) {
      setShowProfile(false);
    }
    if (showNotifications && !event.target.closest('.notifications-container')) {
      setShowNotifications(false);
    }
  };

  useEffect(() => {
    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, [showProfile, showNotifications]);

  return (
    <div className="dashboard">
      {/* Sidebar */}
      <aside className="sidebar">
        <div className="sidebar-header">
          <h2>Taskboard-pro</h2>
        </div>
        <nav className="sidebar-nav">
          <button className="nav-item active"><i className="fas fa-home"></i><span>Dashboard</span></button>
          <button className="nav-item"><i className="fas fa-tasks"></i><span>My Tasks</span></button>
          <button className="nav-item"><i className="fas fa-folder"></i><span>Projects</span></button>
          <button className="nav-item"><i className="fas fa-robot"></i><span>Automations</span></button>
          <div className="sidebar-section-label">COMMUNICATION</div>
          <button className="nav-item"><i className="fas fa-comments"></i><span>Chat</span></button>
          <button className="nav-item"><i className="fas fa-users"></i><span>Team</span></button>
          <div className="sidebar-section-label">ACCOUNT</div>
          <button className="nav-item"><i className="fas fa-user"></i><span>Profile</span></button>
          <button className="nav-item"><i className="fas fa-cog"></i><span>Settings</span></button>
        </nav>
        <div className="sidebar-user">
          <div className="sidebar-avatar">
            {userPhoto ? (
              <img src={userPhoto} alt="Profile" />
            ) : (
              <span>{user?.name?.charAt(0) || user?.email?.charAt(0)}</span>
            )}
          </div>
          <div className="sidebar-user-info">
            <div className="sidebar-user-name">{user?.name}</div>
            <div className="sidebar-user-email">{user?.email}</div>
          </div>
        </div>
      </aside>

      {/* Main Content */}
      <main className="main-content">
        {/* Top Bar */}
        <header className="topbar">
          <div className="search-container">
            <input type="text" className="search-input" placeholder="Search projects, tasks, and team..." />
          </div>
          <div className="topbar-actions">
            <button className="icon-button" onClick={handleThemeToggle} title={isDarkTheme ? "Switch to Light Mode" : "Switch to Dark Mode"}>
              <i className={`fas fa-${isDarkTheme ? 'sun' : 'moon'}`}></i>
            </button>
            <button className="icon-button" onClick={handleNotificationsClick} title="Notifications">
              <i className="fas fa-bell"></i>
              {notifications.length > 0 && <span className="notification-badge">{notifications.length}</span>}
            </button>
            <button className="icon-button profile-btn" onClick={handleProfileClick} title="Profile">
              {userPhoto ? (
                <img src={userPhoto} alt="Profile" className="profile-image" />
              ) : (
                <span className="profile-initial">{user?.name?.charAt(0) || user?.email?.charAt(0)}</span>
              )}
            </button>
          </div>
        </header>

        {/* Welcome Message */}
        <div className="welcome-message">
          <h2>Welcome back, {user?.name}!</h2>
          <p>Here's what's happening in your projects today.</p>
        </div>

        {/* Stat Cards */}
        <section className="dashboard-stats-pro">
          <div className="stat-card-pro">
            <div className="stat-icon stat-projects"><i className="fas fa-folder"></i></div>
            <div className="stat-info">
              <div className="stat-title">Projects</div>
              <div className="stat-value">{projects.length}</div>
            </div>
          </div>
          <div className="stat-card-pro">
            <div className="stat-icon stat-tasks"><i className="fas fa-tasks"></i></div>
