import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/Dashboard.css";

function Dashboard({ user, setUser }) {
  const navigate = useNavigate();
  const [projects, setProjects] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [stats, setStats] = useState({
    totalTasks: 0,
    overdueTasks: 0,
    todayTasks: 0,
    completedTasks: 0
  });
  const [showModal, setShowModal] = useState(false);
  const [showTaskModal, setShowTaskModal] = useState(false);
  const [selectedProject, setSelectedProject] = useState(null);
  const [newProject, setNewProject] = useState({
    title: "",
    description: "",
    teamMembers: []
  });
  const [newTask, setNewTask] = useState({
    title: "",
    description: "",
    project: "",
    dueDate: "",
    priority: "medium"
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [showNotifications, setShowNotifications] = useState(false);
  const [notifications, setNotifications] = useState([
    {
      id: 1,
      message: "New task assigned: Complete project documentation",
      time: "2 hours ago",
      photo: "https://ui-avatars.com/api/?name=John+Doe"
    },
    {
      id: 2,
      message: "Project deadline updated: Marketing Campaign",
      time: "5 hours ago",
      photo: "https://ui-avatars.com/api/?name=Jane+Smith"
    }
  ]);
  const [showProfile, setShowProfile] = useState(false);
  const [userPhoto, setUserPhoto] = useState(null);
  const [isDarkTheme, setIsDarkTheme] = useState(false);

  useEffect(() => {
    fetchProjects();
    fetchTasks();
    fetchStats();
    // Load theme preference from localStorage
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme) {
      setIsDarkTheme(savedTheme === 'dark');
      document.body.classList.toggle('dark-theme', savedTheme === 'dark');
    }

    // Fetch user profile photo
    const fetchUserPhoto = async () => {
      try {
        const response = await fetch('/api/users/profile', {
          credentials: 'include'
        });
        if (response.ok) {
          const data = await response.json();
          setUserPhoto(data.photo || null);
        }
      } catch (error) {
        console.error('Error fetching user photo:', error);
      }
    };

    fetchUserPhoto();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/projects", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setProjects(response.data);
      setError("");
    } catch (error) {
      console.error("Error fetching projects:", error);
      if (error.response?.status === 401) {
        setUser(null);
        navigate("/login", { replace: true });
      } else {
        setError("Failed to fetch projects. Please try again.");
      }
    }
  };

  const fetchTasks = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/tasks/my-tasks", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setTasks(response.data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/tasks/stats", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setStats(response.data);
    } catch (error) {
      console.error("Error fetching stats:", error);
    }
  };

  const handleLogout = async () => {
    setLoading(true);
    try {
      const response = await axios.get("http://localhost:5000/auth/logout", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });

      if (response.data.success) {
        setUser(null);
        navigate("/login", { replace: true });
      } else {
        console.error("Logout failed:", response.data.message);
      }
    } catch (error) {
      console.error("Logout error:", error.response?.data || error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateProject = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/projects", newProject, {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setShowModal(false);
      setNewProject({ title: "", description: "", teamMembers: [] });
      fetchProjects();
    } catch (error) {
      console.error("Error creating project:", error);
      if (error.response?.status === 401) {
        setUser(null);
        navigate("/login", { replace: true });
      } else {
        setError("Failed to create project. Please try again.");
      }
    }
  };

  const handleCreateTask = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/tasks", newTask, {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setShowTaskModal(false);
      setNewTask({
        title: "",
        description: "",
        project: "",
        dueDate: "",
        priority: "medium"
      });
      fetchTasks();
      fetchStats();
    } catch (error) {
      console.error("Error creating task:", error);
      setError("Failed to create task. Please try again.");
    }
  };

  const handleDeleteProject = async (projectId) => {
    if (!window.confirm("Are you sure you want to delete this project?")) {
      return;
    }

    try {
      await axios.delete(`http://localhost:5000/api/projects/${projectId}`, {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      fetchProjects();
    } catch (error) {
      console.error("Error deleting project:", error);
      setError("Failed to delete project. Please try again.");
    }
  };

  const handleTaskStatusChange = async (taskId, newStatus) => {
    try {
      await axios.patch(`http://localhost:5000/api/tasks/${taskId}/status`, 
        { status: newStatus },
        {
          withCredentials: true,
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
          }
        }
      );
      fetchTasks();
      fetchStats();
    } catch (error) {
      console.error("Error updating task status:", error);
      setError("Failed to update task status. Please try again.");
    }
  };

  const handleThemeToggle = () => {
    const newTheme = !isDarkTheme;
    setIsDarkTheme(newTheme);
    localStorage.setItem('theme', newTheme ? 'dark' : 'light');
    document.body.classList.toggle('dark-theme', newTheme);
  };

  const handleProfileClick = () => {
    setShowProfile(!showProfile);
    setShowNotifications(false);
  };

  const handleNotificationsClick = () => {
    setShowNotifications(!showNotifications);
    setShowProfile(false);
  };

  return (
    <div className={`dashboard ${isDarkTheme ? 'dark-theme' : ''}`}>
      <div className="sidebar">
        <div className="sidebar-header">
          <h2>CollabEase</h2>
        </div>
        <nav className="sidebar-nav">
          <button className="nav-item" onClick={() => navigate("/dashboard")}>
            <i className="fas fa-home"></i> Home
          </button>
          <button className="nav-item" onClick={() => navigate("/profile")}>
            <i className="fas fa-user"></i> Profile
          </button>
          <button className="nav-item" onClick={() => navigate("/settings")}>
            <i className="fas fa-cog"></i> Settings
          </button>
          <button className="nav-item" onClick={() => navigate("/calendar")}>
            <i className="fas fa-calendar"></i> Calendar
          </button>
          <button className="nav-item" onClick={() => navigate("/reports")}>
            <i className="fas fa-chart-bar"></i> Reports
          </button>
        </nav>
      </div>

      <div className="main-content">
        <div className="dashboard-header">
          <h1>Dashboard</h1>
          <div className="header-actions">
            <button 
              className="icon-button theme-toggle" 
              onClick={handleThemeToggle}
              title={isDarkTheme ? "Switch to Light Mode" : "Switch to Dark Mode"}
            >
              <i className={`fas fa-${isDarkTheme ? 'sun' : 'moon'}`}></i>
            </button>

            <div className="notifications-container">
              <button 
                className="notification-btn" 
                onClick={() => setShowNotifications(!showNotifications)}
                title="Notifications"
              >
                <i className="fas fa-bell"></i>
                {notifications.length > 0 && (
                  <span className="notification-badge">{notifications.length}</span>
                )}
              </button>

              {showNotifications && (
                <div className="notifications-dropdown">
                  <div className="notifications-header">
                    <h3>Notifications</h3>
                    <button className="mark-all-read">Mark all as read</button>
                  </div>
                  <div className="notifications-list">
                    {notifications.length > 0 ? (
                      notifications.map((notification) => (
                        <div key={notification.id} className="notification-item">
                          <img 
                            src={notification.photo} 
                            alt="Notification" 
                            className="notification-photo"
                          />
                          <div className="notification-content">
                            <p>{notification.message}</p>
                            <span className="notification-time">{notification.time}</span>
                          </div>
                        </div>
                      ))
                    ) : (
                      <div className="no-notifications">No new notifications</div>
                    )}
                  </div>
                </div>
              )}
            </div>

            <div className="profile-container">
              <button 
                className="profile-btn" 
                onClick={() => setShowProfile(!showProfile)}
              >
                {userPhoto ? (
                  <img 
                    src={userPhoto} 
                    alt="Profile" 
                    className="profile-image"
                  />
                ) : (
                  <div className="profile-initial">
                    {user?.name?.charAt(0) || user?.email?.charAt(0)}
                  </div>
                )}
              </button>

              {showProfile && (
                <div className="profile-dropdown">
                  <div className="profile-info">
                    {userPhoto && (
                      <img 
                        src={userPhoto} 
                        alt="Profile" 
                        className="profile-image-large"
                      />
                    )}
                    <h3>{user?.name || 'User'}</h3>
                    <p>{user?.email}</p>
                  </div>
                  <div className="dropdown-divider"></div>
                  <button onClick={() => navigate('/profile')}>
                    <i className="fas fa-user"></i>
                    Profile
                  </button>
                  <button onClick={() => navigate('/settings')}>
                    <i className="fas fa-cog"></i>
                    Settings
                  </button>
                  <div className="dropdown-divider"></div>
                  <button className="logout-btn" onClick={handleLogout}>
                    <i className="fas fa-sign-out-alt"></i>
                    Logout
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>

        {error && <div className="error-message">{error}</div>}

        <div className="dashboard-stats">
          <div className="stat-card">
            <i className="fas fa-tasks"></i>
            <div className="stat-info">
              <h3>Total Tasks</h3>
              <p>{stats.totalTasks}</p>
            </div>
          </div>
          <div className="stat-card">
            <i className="fas fa-exclamation-circle"></i>
            <div className="stat-info">
              <h3>Overdue</h3>
              <p>{stats.overdueTasks}</p>
            </div>
          </div>
          <div className="stat-card">
            <i className="fas fa-calendar-day"></i>
            <div className="stat-info">
              <h3>Due Today</h3>
              <p>{stats.todayTasks}</p>
            </div>
          </div>
          <div className="stat-card">
            <i className="fas fa-check-circle"></i>
            <div className="stat-info">
              <h3>Completed</h3>
              <p>{stats.completedTasks}</p>
            </div>
          </div>
        </div>

        <div className="dashboard-sections">
          <div className="section">
            <div className="section-header">
              <h2>My Projects</h2>
              <button className="add-btn" onClick={() => setShowTaskModal(true)}>
                <i className="fas fa-plus"></i> Add Task
              </button>
            </div>
            <div className="projects-grid">
              {projects.map((project) => (
                <div key={project._id} className="project-card">
                  <div className="project-header">
                    <h3>{project.title}</h3>
                    {project.owner._id === user?._id && (
                      <button 
                        className="delete-btn"
                        onClick={() => handleDeleteProject(project._id)}
                      >
                        <i className="fas fa-trash"></i>
                      </button>
                    )}
                  </div>
                  <p>{project.description}</p>
                  <div className="project-members">
                    <div className="member-list">
                      {project.teamMembers.map((member) => (
                        <div key={member._id} className="member-avatar" title={member.displayName}>
                          {member.image ? (
                            <img src={member.image} alt={member.displayName} />
                          ) : (
                            member.displayName[0]
                          )}
                        </div>
                      ))}
                    </div>
                    <span className="project-status">{project.status}</span>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="section">
            <h2>Recent Tasks</h2>
            <div className="tasks-list">
              {tasks.map((task) => (
                <div key={task._id} className="task-card">
                  <div className="task-header">
                    <h3>{task.title}</h3>
                    <select
                      value={task.status}
                      onChange={(e) => handleTaskStatusChange(task._id, e.target.value)}
                      className={`status-select ${task.status}`}
                    >
                      <option value="todo">To Do</option>
                      <option value="in-progress">In Progress</option>
                      <option value="completed">Completed</option>
                    </select>
                  </div>
                  <p>{task.description}</p>
                  <div className="task-footer">
                    <span className="task-project">{task.project.title}</span>
                    <span className={`task-priority ${task.priority}`}>
                      {task.priority}
                    </span>
                    {task.dueDate && (
                      <span className={`task-due-date ${new Date(task.dueDate) < new Date() ? 'overdue' : ''}`}>
                        Due: {new Date(task.dueDate).toLocaleDateString()}
                      </span>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Create New Project</h2>
            <form onSubmit={handleCreateProject}>
              <input
                type="text"
                placeholder="Project Title"
                value={newProject.title}
                onChange={(e) =>
                  setNewProject({ ...newProject, title: e.target.value })
                }
                required
              />
              <textarea
                placeholder="Project Description"
                value={newProject.description}
                onChange={(e) =>
                  setNewProject({ ...newProject, description: e.target.value })
                }
                required
              />
              <div className="modal-buttons">
                <button type="submit" className="submit-btn">
                  Create
                </button>
                <button
                  type="button"
                  className="cancel-btn"
                  onClick={() => setShowModal(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {showTaskModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Create New Task</h2>
            <form onSubmit={handleCreateTask}>
              <input
                type="text"
                placeholder="Task Title"
                value={newTask.title}
                onChange={(e) =>
                  setNewTask({ ...newTask, title: e.target.value })
                }
                required
              />
              <textarea
                placeholder="Task Description"
                value={newTask.description}
                onChange={(e) =>
                  setNewTask({ ...newTask, description: e.target.value })
                }
                required
              />
              <select
                value={newTask.project}
                onChange={(e) =>
                  setNewTask({ ...newTask, project: e.target.value })
                }
                required
              >
                <option value="">Select Project</option>
                {projects.map((project) => (
                  <option key={project._id} value={project._id}>
                    {project.title}
                  </option>
                ))}
              </select>
              <input
                type="date"
                value={newTask.dueDate}
                onChange={(e) =>
                  setNewTask({ ...newTask, dueDate: e.target.value })
                }
              />
              <select
                value={newTask.priority}
                onChange={(e) =>
                  setNewTask({ ...newTask, priority: e.target.value })
                }
              >
                <option value="low">Low Priority</option>
                <option value="medium">Medium Priority</option>
                <option value="high">High Priority</option>
              </select>
              <div className="modal-buttons">
                <button type="submit" className="submit-btn">
                  Create
                </button>
                <button
                  type="button"
                  className="cancel-btn"
                  onClick={() => setShowTaskModal(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
