import { useState, useEffect, useCallback } from "react";
import { useNavigate, Link, NavLink } from "react-router-dom";
import axios from "axios";
import "../styles/Dashboard.css";
import { FaUserEdit, FaTrash } from 'react-icons/fa';
import Sidebar from '../components/Sidebar';
import Topbar from '../components/Topbar';
import '../styles/global.css';

function Dashboard({ user, setUser }) {
  const navigate = useNavigate();
  const [projects, setProjects] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [stats, setStats] = useState({
    totalTasks: 0,
    overdueTasks: 0,
    todayTasks: 0,
    completedTasks: 0
  });
  const [showNotifications, setShowNotifications] = useState(false);
  const [notifications] = useState([
    {
      id: 1,
      message: "New task assigned: Complete project documentation",
      time: "2 hours ago",
      photo: "https://ui-avatars.com/api/?name=John+Doe"
    },
    {
      id: 2,
      message: "Project deadline updated: Marketing Campaign",
      time: "5 hours ago",
      photo: "https://ui-avatars.com/api/?name=Jane+Smith"
    }
  ]);
  const [showProfile, setShowProfile] = useState(false);
  const [userPhoto, setUserPhoto] = useState(null);
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
  const [recentActivity] = useState([
    {
      id: 1,
      user: { name: 'John Doe', avatar: 'https://ui-avatars.com/api/?name=John+Doe' },
      action: 'completed task "Update Documentation"',
      time: '2 hours ago'
    },
    {
      id: 2,
      user: { name: 'Jane Smith', avatar: 'https://ui-avatars.com/api/?name=Jane+Smith' },
      action: 'added a new comment',
      time: '5 hours ago'
    }
  ]);
  const [showSuccess, setShowSuccess] = useState(false);

  // Team member management states
  const [showAddMember, setShowAddMember] = useState(false);
  const [editingMember, setEditingMember] = useState(null);
  const [newMember, setNewMember] = useState({ name: '', role: '' });

  // Mock team members for demo
  const [teamMembers, setTeamMembers] = useState([
    {
      id: 1,
      name: 'Sanket Jadhav',
      email: 'sanket3280@gmail.com',
      status: 'Active',
      role: 'Member',
      avatar: '',
    },
    {
      id: 2,
      name: 'Sanket tanaji jadhav',
      email: 'sankettanaj.p24@medhaviskil...',
      status: 'Active',
      role: 'Member',
      avatar: '',
    },
    {
      id: 3,
      name: '25F1001327 SANKET JADHAV',
      email: '25f1001327@ds.study.iitm.ac...',
      status: 'Active',
      role: 'Member',
      avatar: '',
    },
    {
      id: 4,
      name: 'Sanket Jadhav',
      email: 'sj546400@gmail.com',
      status: 'Active',
      role: 'Member',
      avatar: 'https://ui-avatars.com/api/?name=Sanket+Jadhav&background=000000&color=fff',
    },
  ]);

  // Team member handlers
  const handleEditMember = (member) => {
    setEditingMember(member);
    setNewMember({ name: member.name, role: member.role });
    setShowAddMember(true);
  };

  const handleDeleteMember = (id) => {
    setTeamMembers(teamMembers.filter(member => member.id !== id));
    setShowSuccess(true);
    setTimeout(() => setShowSuccess(false), 3000);
  };

  const handleMemberSubmit = (e) => {
    e.preventDefault();
    if (editingMember) {
      // Update existing member
      setTeamMembers(teamMembers.map(member =>
        member.id === editingMember.id
          ? { ...member, name: newMember.name, role: newMember.role }
          : member
      ));
    } else {
      // Add new member
      const newId = Math.max(...teamMembers.map(m => m.id)) + 1;
      setTeamMembers([...teamMembers, {
        id: newId,
        name: newMember.name,
        role: newMember.role,
        status: 'Active',
        email: '',
        avatar: `https://ui-avatars.com/api/?name=${encodeURIComponent(newMember.name)}`
      }]);
    }
    setShowAddMember(false);
    setEditingMember(null);
    setNewMember({ name: '', role: '' });
  };

  const fetchProjects = useCallback(async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/projects", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setProjects(response.data);
    } catch (error) {
      console.error("Error fetching projects:", error);
      if (error.response?.status === 401) {
        setUser(null);
        navigate("/login", { replace: true });
      }
    }
  }, [navigate, setUser]);

  const fetchTasks = useCallback(async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/tasks/my-tasks", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setTasks(response.data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  }, []);

  const fetchStats = useCallback(async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/tasks/stats", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setStats(response.data);
    } catch (error) {
      console.error("Error fetching stats:", error);
    }
  }, []);

  useEffect(() => {
    fetchProjects();
    fetchTasks();
    fetchStats();
    // Load theme preference from localStorage
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme) {
      document.body.classList.toggle('dark-theme', savedTheme === 'dark');
    }

    // Fetch user profile photo
    const fetchUserPhoto = async () => {
      try {
        const response = await fetch('/api/users/profile', {
          credentials: 'include'
        });
        if (response.ok) {
          const data = await response.json();
          setUserPhoto(data.photo || null);
        }
      } catch (error) {
        console.error('Error fetching user photo:', error);
      }
    };

    fetchUserPhoto();
  }, [fetchProjects, fetchTasks, fetchStats]);

  const handleLogout = async () => {
    // Immediately clear user and redirect
    setUser(null);
    navigate("/login", { replace: true });

    // Then handle the server logout in the background
    try {
      await axios.get("http://localhost:5000/auth/logout", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
    } catch (error) {
      console.error("Logout error:", error);
    }
  };

  const handleProfileClick = () => {
    setShowProfile(!showProfile);
    setShowNotifications(false);
  };

  const handleProfileNavigation = () => {
    setShowProfile(false);
    navigate('/profile');
  };

  const handleNotificationsClick = () => {
    setShowNotifications(!showNotifications);
    setShowProfile(false);
  };

  const handleClickOutside = useCallback((event) => {
    if (showProfile && !event.target.closest('.profile-container')) {
      setShowProfile(false);
    }
    if (showNotifications && !event.target.closest('.notifications-container')) {
      setShowNotifications(false);
    }
  }, [showProfile, showNotifications]);

  useEffect(() => {
    document.addEventListener('click', handleClickOutside);
    return () => document.removeEventListener('click', handleClickOutside);
  }, [handleClickOutside]);

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col overflow-hidden">
        <Topbar 
          user={user} 
          userPhoto={userPhoto} 
          onProfileClick={handleProfileNavigation} 
          onLogout={handleLogout} 
        />

        <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-100 p-4 sm:p-6">
          {/* Welcome Section */}
          <div className="mb-6">
            <h1 className="text-2xl sm:text-3xl font-bold text-gray-900">
              Welcome back, {user?.displayName || 'User'}!
            </h1>
            <p className="mt-2 text-gray-600">
              Here's what's happening with your projects today.
            </p>
          </div>

          {/* Stats Grid */}
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
            <div className="bg-white p-4 rounded-lg shadow-sm">
              <div className="flex items-center space-x-4">
                <div className="w-12 h-12 rounded-full bg-blue-100 flex items-center justify-center">
                  <i className="fas fa-project-diagram text-blue-600 text-xl"></i>
                </div>
                <div>
                  <h3 className="text-sm text-gray-500">Total Projects</h3>
                  <p className="text-xl sm:text-2xl font-bold">{projects.length}</p>
                </div>
              </div>
            </div>
            <div className="bg-white p-4 rounded-lg shadow-sm">
              <div className="flex items-center space-x-4">
                <div className="w-12 h-12 rounded-full bg-green-100 flex items-center justify-center">
                  <i className="fas fa-tasks text-green-600 text-xl"></i>
                </div>
                <div>
                  <h3 className="text-sm text-gray-500">Active Tasks</h3>
                  <p className="text-xl sm:text-2xl font-bold">{stats.totalTasks}</p>
                </div>
              </div>
            </div>
            <div className="bg-white p-4 rounded-lg shadow-sm">
              <div className="flex items-center space-x-4">
                <div className="w-12 h-12 rounded-full bg-yellow-100 flex items-center justify-center">
                  <i className="fas fa-users text-yellow-600 text-xl"></i>
                </div>
                <div>
                  <h3 className="text-sm text-gray-500">Team Members</h3>
                  <p className="text-xl sm:text-2xl font-bold">{teamMembers.length}</p>
                </div>
              </div>
            </div>
            <div className="bg-white p-4 rounded-lg shadow-sm">
              <div className="flex items-center space-x-4">
                <div className="w-12 h-12 rounded-full bg-purple-100 flex items-center justify-center">
                  <i className="fas fa-chart-line text-purple-600 text-xl"></i>
                </div>
                <div>
                  <h3 className="text-sm text-gray-500">Completion Rate</h3>
                  <p className="text-xl sm:text-2xl font-bold">{((stats.completedTasks / stats.totalTasks) * 100).toFixed(2)}%</p>
                </div>
              </div>
            </div>
          </div>

          {/* Recent Activity and Team Members */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Recent Activity */}
            <div className="bg-white rounded-lg shadow-sm">
              <div className="p-4 sm:p-6">
                <h2 className="text-lg sm:text-xl font-semibold mb-4">Recent Activity</h2>
                <div className="space-y-4">
                  {recentActivity.map((activity) => (
                    <div key={activity.id} className="flex items-start space-x-4">
                      <div className="flex-shrink-0">
                        <img
                          src={activity.user.avatar}
                          alt={activity.user.name}
                          className="w-8 h-8 rounded-full"
                        />
                      </div>
                      <div className="flex-1 min-w-0">
                        <p className="text-sm text-gray-900">
                          <span className="font-medium">{activity.user.name}</span>{' '}
                          {activity.action}
                        </p>
                        <p className="text-xs text-gray-500 mt-1">{activity.time}</p>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>

            {/* Team Members */}
            <div className="bg-white rounded-lg shadow-sm">
              <div className="p-4 sm:p-6">
                <div className="flex justify-between items-center mb-4">
                  <h2 className="text-lg sm:text-xl font-semibold">Team Members</h2>
                  <button
                    onClick={() => setShowAddMember(true)}
                    className="px-3 py-1 text-sm bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
                  >
                    Add Member
                  </button>
                </div>
                <div className="space-y-4">
                  {teamMembers.map((member) => (
                    <div key={member.id} className="flex items-center justify-between">
                      <div className="flex items-center space-x-3">
                        <img
                          src={member.avatar}
                          alt={member.name}
                          className="w-10 h-10 rounded-full"
                        />
                        <div>
                          <h3 className="text-sm font-medium text-gray-900">{member.name}</h3>
                          <p className="text-xs text-gray-500">{member.role}</p>
                        </div>
                      </div>
                      <div className="flex items-center space-x-2">
                        <button
                          onClick={() => handleEditMember(member)}
                          className="p-1 text-gray-400 hover:text-gray-600"
                        >
                          <i className="fas fa-edit"></i>
                        </button>
                        <button
                          onClick={() => handleDeleteMember(member.id)}
                          className="p-1 text-gray-400 hover:text-red-600"
                        >
                          <i className="fas fa-trash"></i>
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>

          {/* Add/Edit Member Modal */}
          {showAddMember && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
              <div className="bg-white rounded-lg p-4 sm:p-6 w-full max-w-md">
                <h3 className="text-lg sm:text-xl font-bold mb-4">
                  {editingMember ? 'Edit Team Member' : 'Add Team Member'}
                </h3>
                <form onSubmit={handleMemberSubmit} className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Name
                    </label>
                    <input
                      type="text"
                      value={newMember.name}
                      onChange={(e) => setNewMember({ ...newMember, name: e.target.value })}
                      className="w-full px-3 py-2 text-sm border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Role
                    </label>
                    <input
                      type="text"
                      value={newMember.role}
                      onChange={(e) => setNewMember({ ...newMember, role: e.target.value })}
                      className="w-full px-3 py-2 text-sm border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      required
                    />
                  </div>
                  <div className="flex justify-end space-x-3">
                    <button
                      type="button"
                      onClick={() => {
                        setShowAddMember(false);
                        setEditingMember(null);
                        setNewMember({ name: '', role: '' });
                      }}
                      className="px-4 py-2 text-sm text-gray-700 bg-gray-100 rounded-lg hover:bg-gray-200 transition-colors"
                    >
                      Cancel
                    </button>
                    <button
                      type="submit"
                      className="px-4 py-2 text-sm text-white bg-blue-500 rounded-lg hover:bg-blue-600 transition-colors"
                    >
                      {editingMember ? 'Save Changes' : 'Add Member'}
                    </button>
                  </div>
                </form>
              </div>
            </div>
          )}
        </main>
      </div>
    </div>
  );
}

export default Dashboard;
